data.lvlindex[EQUIP_INDEX.FOOT]={}
data.lvlindex[EQUIP_INDEX.DECORATIONS]={}
--按等级
data.lvl={}

local function init()
	-- 装备排列
	for i,v in pairs(data.equipinfo) do
		if v.requirelist.lvl <= CURR_EQUIPMENT_MAX_LVL then
			table.insert(data[v.index],v)
			table.insert(data.allEquip,v)
			--等级+部位
			data.lvlindex[v.index][v.requirelist.lvl] = data.lvlindex[v.index][v.requirelist.lvl] or {}
			table.insert(data.lvlindex[v.index][v.requirelist.lvl],v)
			--等级
			data.lvl[v.requirelist.lvl] = data.lvl[v.requirelist.lvl] or {}
			table.insert(data.lvl[v.requirelist.lvl],v)
		end
	end
end

init()

return data